import React, { useState, useEffect } from 'react';
import axios from 'axios';
import QRCode from 'qrcode.react';
import QrScanner from 'react-qr-scanner';
import './App.css';
import wifiIcon from './wifi_PNG62346.png';
import playIcon from './play_icon.png';
import forwardArrowIcon from './forward_arrow_icon.png';

function App() {
  const [devices, setDevices] = useState([]);
  const [showSerial, setShowSerial] = useState(false);
  const [wifiNetworks, setWifiNetworks] = useState([]);
  const [selectedDeviceId, setSelectedDeviceId] = useState(null);

  const fetchDevices = async () => {
    try {
      const response = await axios.get('http://localhost:4000/devices');
      setDevices(response.data);
    } catch (error) {
      console.error('Error fetching devices:', error);
    }
  };

  const startMirroring = async (deviceId) => {
    try {
      const response = await axios.get(`http://localhost:4000/mirror/usb/${deviceId}`);
      console.log(response.data);
    } catch (error) {
      console.error('Error starting screen mirroring:', error);
    }
  };

  const scanWifi = async (deviceId) => {
    try {
      const response = await axios.get(`http://localhost:4000/wifi/${deviceId}`);
      setWifiNetworks(response.data);
      setSelectedDeviceId(deviceId);
    } catch (error) {
      console.error('Error scanning WiFi networks:', error);
    }
  };

  const connectWifi = async (ssid, password) => {
    try {
      await axios.post('http://localhost:4000/connect_wifi', { ssid, password });
      setWifiNetworks([]);
      startMirroring(selectedDeviceId); // Start mirroring after connecting to WiFi
    } catch (error) {
      console.error('Error connecting to WiFi:', error);
    }
  };

  const handleScan = async (data) => {
    if (data) {
      try {
        await axios.post('http://localhost:4000/qr-mirror', { data });
      } catch (error) {
        console.error('Error starting mirroring via QR:', error);
      }
    }
  };

  const handleError = (err) => {
    console.error(err);
  };

  useEffect(() => {
    fetchDevices();
  }, []);

  return (
    <div className="App">
      <div className="container">
        <div className="box green-box">
          <h2>Android Devices</h2>
          <p>No Android devices are connected. Please ensure the Android device is connected with a data cable, and USB Debugging is enabled.</p>
          <p>Android not found? Make sure <a href="#">Android USB Debugging</a> is enabled. Windows users need to install <a href="#">ADB Drivers</a>.</p>
          <p>Using Android SDK ADB server. <button onClick={fetchDevices}>Force Restart</button></p>
          {devices.length > 0 && (
            <div>
              <h3>Connected Devices:</h3>
              <ul>
                {devices.map(device => (
                  <li key={device.id} className="device-list-item">
                    <span>{showSerial ? device.id : device.name}</span>
                    <img
                      src={playIcon}
                      alt="Play"
                      className="icon"
                      onClick={() => startMirroring(device.id)}
                    />
                    <img
                      src={wifiIcon}
                      alt="WiFi"
                      className="icon"
                      onClick={() => scanWifi(device.id)}
                    />
                    <img
                      src={forwardArrowIcon}
                      alt="Forward Arrow"
                      className="icon"
                    />
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
        <div className="box middle-box">
          <h2>Connect by scanning the QR code</h2>
          <div className="qr-code">
            {selectedDeviceId ? (
              <QRCode value={`mirror:${selectedDeviceId}`} size={180} />
            ) : (
              <p>Connect a device to generate QR code</p>
            )}
          </div>
          <QrScanner
            delay={300}
            onError={handleError}
            onScan={handleScan}
            style={{ width: '100%' }}
          />
        </div>
        <div className="box blue-box">
          <h2>iOS Devices</h2>
          <p>No iOS devices are connected. Please ensure the iOS device is connected with a data cable, and USB Debugging is enabled.</p>
          <p>iOS not found? Make sure <a href="#">iOS USB Debugging</a> is enabled. Windows users need to install <a href="#">ADB Drivers</a>.</p>
          <p>Using iOS SDK ADB server. <button onClick={fetchDevices}>Force Restart</button></p>
        </div>
      </div>
      {wifiNetworks.length > 0 && (
        <div className="wifi-popup">
          <h3>Select WiFi Network</h3>
          <ul>
            {wifiNetworks.map((network, index) => (
              <li key={index} onClick={() => connectWifi(network.ssid, prompt('Enter password for ' + network.ssid))}>
                {network.ssid}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
